-rw-r--r-- 1 root root  2344 Jan 10 01:49 httpd-default.conf&lt;br /&gt;
-rw-r--r-- 1 root root  1103 Jan 10 01:49 httpd-info.conf&lt;br /&gt;
-rw-r--r-- 1 root root  5078 Jan 10 01:49 httpd-languages.conf&lt;br /&gt;
-rw-r--r-- 1 root root   932 Jan 10 01:49 httpd-manual.conf&lt;br /&gt;
-rw-r--r-- 1 root root  3789 Jan 10 01:49 httpd-mpm.conf&lt;br /&gt;
