-rw-r--r-- 1 root root  1507 Jan 10 01:49 httpd-vhosts.conf # 虚拟主机配置文件&lt;/p&gt;
&lt;hr /&gt;
&lt;h1 id=&#34;三-httpd-vhostsconf虚拟主机配置文件注释&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#三-httpd-vhostsconf虚拟主机配置文件注释&#34;&gt;#&lt;/a&gt; 三、httpd-vhosts.conf 虚拟主机配置文件注释&lt;/h1&gt;
&lt;p&gt;[root@extra]# egrep -v &amp;quot;&lt;sup&gt;.*#|&lt;/sup&gt;$&amp;quot; httpd-vhosts.conf |nl&lt;br /&gt;
1  NameVirtualHost *:80                                # 基于名称的虚拟主机配置（ *:80 表示监听本机所有 ip）&lt;br /&gt;
2  &amp;lt;VirtualHost *:80&amp;gt;                                  # 定义一个虚拟主机&lt;br /&gt;
 3      ServerAdmin &lt;span class=&#34;exturl&#34; data-url=&#34;bWFpbHRvOndlYm1hc3RlckBkdW1teS1ob3N0LmV4YW1wbGUuY29t&#34;&gt;webmaster@dummy-host.example.com&lt;/span&gt;    # 配置管理员邮箱&lt;br /&gt;
 4      DocumentRoot &amp;quot;/application/apache2.2.22/docs/dummy-host.example.com&amp;quot;  # 程序的站点目录&lt;br /&gt;
 5      ServerName &lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cDovL2R1bW15LWhvc3QuZXhhbXBsZS5jb20=&#34;&gt;dummy-host.example.com&lt;/span&gt;               # 域名服务，需要 apache mode_alias 模块支持&lt;br /&gt;
 6      ServerAlias &lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cDovL3d3dy5kdW1teS1ob3N0LmV4YW1wbGUuY29t&#34;&gt;www.dummy-host.example.com&lt;/span&gt;          # 虚拟主机别名&lt;br /&gt;
 7      ErrorLog &amp;quot;logs/dummy-host.example.com-error_log&amp;quot;# 错误的日志路径&lt;br /&gt;
 8      CustomLog &amp;quot;logs/dummy-host.example.com-access_log&amp;quot; common  #访问日志配置（生产环境我们用 combined 格式代替 common 格式）&lt;br /&gt;
9  &amp;lt;/VirtualHost&amp;gt;&lt;br /&gt;
10  &amp;lt;VirtualHost *:80&amp;gt;&lt;br /&gt;
11      ServerAdmin &lt;span class=&#34;exturl&#34; data-url=&#34;bWFpbHRvOndlYm1hc3RlckBkdW1teS1ob3N0Mi5leGFtcGxlLmNvbQ==&#34;&gt;webmaster@dummy-host2.example.com&lt;/span&gt;&lt;br /&gt;
12      DocumentRoot &amp;quot;/application/apache2.2.22/docs/dummy-host2.example.com&amp;quot;&lt;br /&gt;
13      ServerName &lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cDovL2R1bW15LWhvc3QyLmV4YW1wbGUuY29t&#34;&gt;dummy-host2.example.com&lt;/span&gt;&lt;br /&gt;
14      ErrorLog &amp;quot;logs/dummy-host2.example.com-error_log&amp;quot;&lt;br /&gt;
15      CustomLog &amp;quot;logs/dummy-host2.example.com-access_log&amp;quot; common&lt;br /&gt;
16  &amp;lt;/VirtualHost&amp;gt;&lt;/p&gt;
