REG ADD &amp;quot;HKCU\Environment&amp;quot; /v &amp;quot;COR_ENABLE_PROFILING&amp;quot; /t REG_SZ /d &amp;quot;1&amp;quot; /f
REG ADD &amp;quot;HKCU\Environment&amp;quot; /v &amp;quot;COR_PROFILER_PATH&amp;quot; /t REG_SZ /d &amp;quot;C:\Temp\test.dll&amp;quot; /f
